require('./index.html');
require('./style.scss');

const SCREEN_WIDTH = 80;
const SCREEN_HEIGHT = 60;
const SCALE_FACTOR = 8;
const FRAME_SKIP = 8;
const GREENS = [ "#8BC34A", "#7CB342", "#CDDC39", "#D4E157" ];
let frameSkipCounter = 0;
let is_running = true;
let lastFrame = 0;
let animationInterval = 10;
let animationCounter = 0;

let elem = document.getElementById("root");
elem.width = SCREEN_WIDTH * SCALE_FACTOR;
elem.height = SCREEN_HEIGHT * SCALE_FACTOR;

let canvas = elem.getContext("2d");

const clearScreen = () => {
  if (canvas) {
    canvas.fillStyle = '#FFF';
    canvas.fillRect(0, 0, SCREEN_WIDTH * SCALE_FACTOR, SCREEN_HEIGHT * SCALE_FACTOR);
  }
};

const drawPixel = (x, y) => {
  let color = GREENS[0];
  if (x % 3 == 0) color = (y % 2 == 0) ? GREENS[1] : GREENS[0];
  if (x % 2 == 0) color = (y % 2 == 0) ? GREENS[2] : GREENS[1];
  if (y % 2 == 0) color = GREENS[2];
  if (y % 3 == 0) color = GREENS[3];
  canvas.fillStyle = color;
  canvas.fillRect(x * 10, y * 10, 10, 10);
  canvas.strokeStyle = '#FFF';
  canvas.strokeRect(x * 10, y * 10, 10, 10);
};

const highLight = (x, y) => {
  canvas.fillStyle = '#F00';
  canvas.fillRect(x, y, 10, 10);
};

const mainLoop = (time) => {
  let deltaTime = time - lastFrame;
  lastFrame = time;
  clearScreen();

  animationCounter += deltaTime;
  if (animationCounter >= animationInterval) {
    drawGame();
    if (is_running) {
      frameSkipCounter += 1;
      if (frameSkipCounter >= FRAME_SKIP) {
        board = gameOfLife(board);
        frameSkipCounter = 0;
      }
    }
    animationCounter = 0;
  }

  requestAnimationFrame(mainLoop);
};

mainLoop(0);

