let current_highlight = -1;
let board = JSON.parse(GLIDER_GUN_PATTERN);
//let board = Array.from(Array(SCREEN_WIDTH * SCREEN_HEIGHT)).map(x => 0);

const drawGame = () => {
  for (let i = 0; i < SCREEN_WIDTH * SCREEN_HEIGHT; i++) {
    let pos = getXY(i);
    let x = pos.x, y = pos.y;
    if (board[i] == 1) drawPixel(x, y);
    if (i == current_highlight) {
      highLight(x, y);
    }
  }
};

const getXY = (n) => {
  return { x: (n % SCREEN_WIDTH), y: (~~(n / SCREEN_WIDTH)) }
};

const getCell = (board, x, y) => {
  if (x < 0 || y < 0) return -1;
  if (x > SCREEN_WIDTH || y > SCREEN_HEIGHT) return -1;
  let pos = SCREEN_WIDTH * y + x;
  return board[pos];
};

const countNeighbours = (board, x, y) => {
  let top_left = getCell(board, x-1, y-1) == 1 ? 1 : 0;
  let left = getCell(board, x-1, y) == 1 ? 1 : 0;
  let bottom_left = getCell(board, x-1, y+1) == 1 ? 1 : 0;
  let top = getCell(board, x, y-1) == 1 ? 1 : 0;
  let top_right = getCell(board, x+1, y-1) == 1 ? 1 : 0;
  let right = getCell(board, x+1, y) == 1 ? 1 : 0;
  let bottom_right = getCell(board, x+1, y+1) == 1 ? 1 : 0;
  let bottom = getCell(board, x, y+1) == 1 ? 1 : 0;
  return top_left + left + bottom_left + top + top_right + right + bottom_right + bottom;
};

const gameOfLife = (b) => {
  let check = b.map(x => 0);
  for (let i = 0; i < SCREEN_HEIGHT * SCREEN_WIDTH; i++) {
    let pos = getXY(i);
    let x = pos.x; let y = pos.y;
    check[i] = countNeighbours(b, x, y);
  }
  for (let j = 0; j < SCREEN_HEIGHT * SCREEN_WIDTH; j++) {
    let pos = getXY(j);
    let x = pos.x; let y = pos.y;
    if (b[j] == 1) {
      if (check[j] < 2) b[j] = 0;
      if (check[j] > 3) b[j] = 0;
      if (check[j] == 2 || check[j] == 3) b[j] = 1;
    } else if (b[j] == 0) {
      if (check[j] == 3) b[j] = 1;
    }
  }
  return b;
};

elem.addEventListener("mousedown", (e) => {
  let mx = ~~(e.layerX / SCALE_FACTOR); let my = ~~(e.layerY / SCALE_FACTOR);
  let pos = SCREEN_WIDTH * my + mx;
  board[pos] = board[pos] > 0 ? 0 : 1;
});

elem.addEventListener("mousemove", (e) => {
  let mx = ~~(e.layerX / SCALE_FACTOR); let my = ~~(e.layerY / SCALE_FACTOR);
  let pos = SCREEN_WIDTH * my + mx;
  current_highlight = pos;
});

window.addEventListener("keypress", (e) => {
  let keyCode = e.keyCode || e.which;
  if (keyCode == 13) {
    is_running = !is_running;
  }
});
